choose.when:
SELECT T1.SUP_INFO_ID SUP_ID,T2.SUP_NAME FROM T_ENTERPRISE_SUP_MAPPING T1 LEFT OUTER JOIN T_SUP_INFO T2 ON T1.SUP_INFO_ID=T2.SUP_ID WHERE T1.IF_BUILDING=0 AND T2.SUP_TYPE='1' AND t2.if_valid = '1' AND T1.COMPANY_ID = #{company_id,jdbcType=VARCHAR}
AND T2.SUP_NAME LIKE '%' || #{sup_name} || '%'
ORDER BY T2.SUP_NAME

