mapper.select:
select * from (
select row_number() over (order by invoice_status asc, pay_time desc) rn, order_id, addr_all, addr_name, addr_phone, addr_tel, order_amount - refund_price as order_amount, order_amount_init - refund_price_init as order_amount_init, pay_time, invoice_status, express_name, express_code, invoice_type, buy_num - refund_num as goods_num, refunding, companyid, companyname from ( select a.order_id, addr_all, addr_name, addr_phone, addr_tel, order_amount, pay_time, invoice_status, express_name, express_code, invoice_type, b.companyid, b.companyname, a.order_amount_init, (select sum(buy_num) from t_order_snapshot c where c.order_id = a.order_id) as buy_num, (select nvl(sum(refound_amount), 0) from t_order_refound d where d.order_id = a.order_id and d.refound_status = '5') as refund_num, (select nvl(sum(refound_price), 0) from t_order_refound e where e.order_id = a.order_id and e.refound_status = '5') as refund_price, (select nvl(sum(refound_price_init), 0) from t_order_refound f where f.order_id = a.order_id and f.refound_status = '5') as refund_price_init, (select count(1) from t_order_refound f where f.order_id = a.order_id and f.refound_status <> '5' and f.refound_status <> '2' and f.refound_status <> '6') as refunding
from t_order a,users b where a.user_id = b.userid and order_status = 20
and b.companyid = #{companyId}
and a.order_id like '%'||#{orderId}||'%'
and pay_time >= to_date(#{timeStart}, 'yyyy-MM-dd HH24:mi:ss')
and pay_time <= to_date(#{timeEnd}, 'yyyy-MM-dd HH24:mi:ss')
and invoice_status = #{invoiceStatus}
) where buy_num > refund_num ) x
where x.rn between #{start} and #{end}

