mapper.select:
select * from (select R.*, rownum rn from (select ID, NOTICE_SUBJECT, NOTICE_TYPE, to_char(BEGIN_DATE,'yyyy-mm-dd') BEGIN_DATE, to_char(END_DATE,'yyyy-mm-dd') END_DATE, CONTENT, CREATE_DATE, CREATE_USER, BROWSE_COUNT, IS_PUBLISH, PUBLISH_DATE, IS_TOP from T_WEBSITE_NOTICE where 1=1
and NOTICE_SUBJECT like '%'||#{noticeSubject,jdbcType=VARCHAR}||'%'
and NOTICE_TYPE=#{noticeType,jdbcType=VARCHAR}
and id=#{id,jdbcType=VARCHAR}
AND TO_CHAR(PUBLISH_DATE,'yyyy-MM-dd') >= #{publishSatrDate,jdbcType=VARCHAR}
AND TO_CHAR(PUBLISH_DATE, 'yyyy-MM-dd') <= #{publishEndDate,jdbcType=VARCHAR}
order by CREATE_DATE desc) R) WHERE RN BETWEEN #{pageFrom, jdbcType = INTEGER} AND #{pageTo, jdbcType = INTEGER}

select count(1)
                  from T_WEBSITE_NOTICE
                  where 1=1

		                and NOTICE_SUBJECT like '%'||#{noticeSubject,jdbcType=VARCHAR}||'%'


		                and NOTICE_TYPE=#{noticeType,jdbcType=VARCHAR}


		                and id=#{id,jdbcType=VARCHAR}


					  AND TO_CHAR(PUBLISH_DATE,'yyyy-MM-dd') >= #{publishSatrDate,jdbcType=VARCHAR}


					  AND TO_CHAR(PUBLISH_DATE, 'yyyy-MM-dd') <= #{publishEndDate,jdbcType=VARCHAR}

mapper.update:
update T_WEBSITE_NOTICE set 

	IS_PUBLISH = #{isPublish,jdbcType=VARCHAR} 


	IS_TOP = #{isTop,jdbcType=VARCHAR} 


	,PUBLISH_DATE = sysdate 



where id=#{id,jdbcType=VARCHAR}

update T_WEBSITE_NOTICE set  BEGIN_DATE = to_date(#{beginDate,jdbcType=VARCHAR},'yyyy-mm-dd')
, END_DATE = to_date(#{endDate,jdbcType=VARCHAR},'yyyy-mm-dd')
, CONTENT = #{content,jdbcType=BLOB}
,NOTICE_TYPE = #{noticeType,jdbcType=VARCHAR}
,NOTICE_SUBJECT = #{noticeSubject,jdbcType=VARCHAR}

	,IS_PUBLISH = #{isPublish,jdbcType=VARCHAR} 
	,PUBLISH_DATE = sysdate

where id=#{id,jdbcType=VARCHAR}

mapper.insert:
insert into T_WEBSITE_NOTICE (ID, NOTICE_SUBJECT, NOTICE_TYPE, BEGIN_DATE, END_DATE, CONTENT, IS_TOP, CREATE_USER
,IS_PUBLISH ,PUBLISH_DATE
) values( to_char(sysdate, 'yyyyMMddHH24miss') || lpad(NOTICE_INDEX.nextval, 6, '0'), #{noticeSubject,jdbcType=VARCHAR}, #{noticeType,jdbcType=VARCHAR}, to_date(#{beginDate,jdbcType=VARCHAR},'yyyy-mm-dd'), to_date(#{endDate,jdbcType=VARCHAR},'yyyy-mm-dd'), #{content,jdbcType=BLOB}, '0', #{createUser,jdbcType=VARCHAR}
,#{isPublish,jdbcType=VARCHAR} ,sysdate
)
